/////////////////////////////////////////
//  Functionality: Two FFs share a differential pair of reset. One is controlled by an inversion of the reset, while the other is controlled by the original reset. This is useful to validate 
//                 - reset signal to drive LUT from global network
//                 - reset signal generated by internal programmable resources
//  Author:        Xifan Tang
////////////////////////////////////////
`timescale 1ns / 1ps

module two_dff_inv_rst(
  clk_i,
  rst_i,
  d_i,
  d0_o, d1_o);
input wire clk_i;
input wire rst_i;
input wire d_i;
output reg d0_o;
output reg d1_o;

wire int_rst;

assign int_rst = ~rst_i;

always @(posedge clk_i or posedge rst_i) begin
  if (rst_i) begin
    d0_o <= 0;
  end else begin
    d0_o <= d_i;
  end
end

always @(posedge clk_i or posedge int_rst) begin
  if (int_rst) begin
    d1_o <= 0;
  end else begin
    d1_o <= d_i;
  end
end


endmodule
